import React, { Component } from 'react';
import Todos from './components/Todos';
import './App.css';
import Header from './components/layout/Header';

class App extends Component {
  state={ //state is an object that dictates how comp behaves and what it renders
    todos:[ //this is an array of objects that will be passed and rendered to one comp
      {
        id: 1,
        title: 'Take out the trash',
        completed: false
      },
      {
        id: 2,
        title: 'Eat',
        completed: false
      },
      {
        id: 3,
        title: 'Meet with boss',
        completed: false
      }
    ]
  }

  markComplete=(id)=>{ //toggle complete
    this.setState({todos: this.state.todos.map(todo=>{
      if(todo.id === id){
        todo.completed = !todo.completed;
      }
      return todo;
    })})
  }

  delTodo=(id)=>{
    this.setState({todos: [...this.state.todos.filter(todo=>todo.id!==id)]})
  }

  render(){
    //pass prop with {this.state.comp}
    return (
      <div className="App">
        <Header></Header>
        <Todos todos={this.state.todos} markComplete={this.markComplete} delTodo={this.delTodo}></Todos>
      </div>
    );
  }
}

export default App;
