import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Todos from './components/Todos';
import './App.css';
import Header from './components/layout/Header';
import AddTodo from './components/AddTodo';

import { v4 as uuid } from 'uuid';

class App extends Component {
	state = {
		//state is an object that dictates how comp behaves and what it renders
		todos: [
			//this is an array of objects that will be passed and rendered to one comp
			{
				id: uuid(),
				title: 'Take out the trash',
				completed: false,
			},
			{
				id: uuid(),
				title: 'Eat',
				completed: false,
			},
			{
				id: uuid(),
				title: 'Meet with boss',
				completed: false,
			},
		],
	};

	markComplete = (id) => {
		//toggle complete
		this.setState({
			todos: this.state.todos.map((todo) => {
				if (todo.id === id) {
					todo.completed = !todo.completed;
				}
				return todo;
			}),
		});
	};

	delTodo = (id) => {
		this.setState({
			todos: [...this.state.todos.filter((todo) => todo.id !== id)],
		});
	};

	addTodo = (title) => {
		const newTodo = {
			id: uuid(),
			title: title,
			completed: false,
		};
		this.setState({ todos: [...this.state.todos, newTodo] });
	};

	render() {
		//pass prop with {this.state.comp}
		return (
			//surround all with Router
			<Router>
				<div className="App">
					<div className="Container">
						<Header></Header>
						{/* this is a route, put components rendered in that route there and specify path and render func */}
						<Route
							path="/"
							render={(
								props //this the func that will render the 2 components AddTodo and Todos
							) => (
								<React.Fragment>
									<AddTodo addTodo={this.addTodo}></AddTodo>
									<Todos
										todos={this.state.todos}
										//send functions as props
										markComplete={this.markComplete}
										delTodo={this.delTodo}
									></Todos>
								</React.Fragment>
							)}
						></Route>
					</div>
				</div>
			</Router>
		);
	}
}

export default App;
