import React, { Component } from 'react';
import Todos from './components/Todos';
import './App.css';

class App extends Component {
  state={ //state is an object that dictates how comp behaves and what it renders
    todos:[ //this is an array of objects that will be passed and rendered to one comp
      {
        id: 1,
        title: 'Take out the trash',
        completed: true
      },
      {
        id: 2,
        title: 'Eat',
        completed: false
      },
      {
        id: 3,
        title: 'Meet with boss',
        completed: false
      }
    ]
  }

  markComplete=(id)=>{
    console.log(id);
  }

  render(){
    //pass prop with {this.state.comp}
    return (
      <div className="App">
        <Todos todos={this.state.todos} markComplete={this.markComplete}></Todos>
      </div>
    );
  }
}

export default App;
